INSERT INTO tours (title,duration,startTime,rating, hotel, price, vehicle, type, numberOfPeople, description, numberOfBooking, image, isTrending)
VALUES	('Tour Pháp','5 ngày','2021-12-11', '5', 'Paris', '800', 'Máy bay' ,'Nước ngoài', '5-8', 'Pháp – đất nước được mệnh danh là kinh đô Ánh sáng luôn là điểm đến mà bao người mong muốn đặt chân đến ít nhất một lần. Quốc gia lớn thứ nhất khu vực Tây Âu và thứ ba châu Âu này sở hữu vẻ đẹp tráng lệ của những công trình kiến trúc ấn tượng, cảnh sắc hùng vĩ cùng nền văn hóa, ẩm thực phong phú, đặc sắc,… đã thu hút hàng nghìn du khách từ khắp mọi nơi. Nhưng đây không phải là nơi dễ đến, bạn phải có kinh nghiệm du lịch Pháp mới có thể khám phá trọn vẹn đất nước xinh đẹp này. Đến Pháp cần chuẩn bị gì, đi lại như thế nào, tham quan những đâu hay ăn gì,… là những băn khoăn mà phần lớn ai cũng thắc mắc khi có ý định đặt chân đến quốc gia lãng mạn này. Đừng lo, tất cả sẽ được thông tin chi tiết ngay dưới đây, các bạn có thể tham khảo trước khi thực hiện chuyến đi.','5', '/images/tour/france.jpg', true),
		('Tour Bali','4 ngày','2021-12-10',  '4', 'Love Night', '342' ,'Tàu hỏa', 'Nước ngoài', '9-12', 'Hòn đảo xinh đẹp Bali của quốc gia vạn đảo Indonesia đã quá nổi tiếng trên bản đồ du lịch Thế Giới đặc biệt là dành cho các kỳ nghỉ trăng mật. Vì sao du lịch Bali lại hấp dẫn như vậy bởi nơi đây có rất nhiều điểm đặc sắc như khí hậu nhiệt đới ấm áp, có biển xanh, nắng vàng, nhiều hòn đảo đẹp, văn hóa - tôn giáo đa dạng, nhiều điểm tham quan và quan trọng là dịch vụ du lịch rất phát triển.','6', '/images/tour/bali.jpg', true),
		('Trải nghiệm Thailand', '7 ngày', '2023-1-1', '3', 'Vjp Hotel', '942', 'Xe buýt' ,'Nước ngoài', '1-4', 'Thái Lan là quốc gia có phần đông dân số theo đạo Phật. Đất nước này dĩ nhiên có những điểm độc đáo riêng thu hút khách du lịch, như các công trình lịch sử và tôn giáo với tượng Phật ở khắp mọi ngõ ngách trong thành phố. Nhưng ngoài các địa danh tôn giáo nguy nga, Thái Lan còn tiếp đón bạn với các thắng cảnh thiên nhiên, nền ẩm thực hấp dẫn và tất nhiên là hằng hà sa số trung tâm mua sắm với giá cả phải chăng nữa.','12', '/images/tour/thailand.jpg', true),
		('Tour Turkey', '10 ngày', '2022-2-2', '5', 'Swinburn View', '1442', 'Máy bay' ,'Nước ngoài', '9-12', 'Du lịch Thổ Nhĩ Kỳ - Thổ Nhĩ Kỳ, tên chính thức là nước Cộng hòa Thổ Nhĩ Kỳ, là một quốc gia xuyên lục địa, phần lớn nằm tại Tây Á và một phần nằm tại Đông Nam Âu. Đến với đất nước Thổ Nhĩ Kỳ du khách sẽ có dịp quay ngược thời gian trở về thời cổ đại qua nhiều di sản, công trình tàn tích trong các sử thi hào hùng,... Tour du lịch Thổ Nhĩ Kỳ thực sự sẽ mang lại cho du khách những cuộc phiêu lưu mạo hiểm kỳ thú. Để hiểu hơn về đất nước này các bạn hãy đến với tour Thổ Nhĩ Kỳ tại Công ty Du Lịch Việt để điểm qua những thông tin dưới đây giúp bạn có một chuyến đi lí thú và bổ ích.','2', '/images/tour/turkey.jpg', true),
		('Ha Noi Travel', '6 ngày', '2022-1-12', '4', 'Luxurious Hotel', '750', 'Tàu hỏa' ,'Trong nước', '13-16', 'Hà Nội trà chanh chém gió, Hà Nội trà đá vỉa hè… Dường như những câu miêu tả đơn giản này lại có thể gom hết những điều bình dị nhất của mảnh đất thủ đô. Cùng với lịch sử “nghìn năm văn hiến”, Hà Nội luôn thu hút du khách với một màu sắc đặc trưng, một nét văn hóa đậm đà bản sắc, và một lối sống riêng chẳng thể lẫn với bất kỳ nơi nào khác. Nếu tò mò về “màu sắc” ấy, hãy lưu lại những kinh nghiệm du lịch Hà Nội dưới đây và lên lịch cho chuyến khám phá thủ đô thôi nào!','4', '/images/tour/hanoi.jpg', true),
		('Vòng quanh Canada', '10 ngày', '2022-3-8', '4', 'Toronto Tokyo', '600', 'Máy bay' ,'Nước ngoài', '1-4', 'Với diện tích lớn thứ 2 thế giới, Canada sẽ là điểm đến thu hút đông du khách quốc tế bởi những cảnh quan thiên nhiên tuyệt đẹp, những công trình kiến trúc hiện đại nhưng cũng không kém phần lãng mạng. Đến Canada bằng tour Canada, du khách sẽ được thả hồn vào quang cảnh thiên nhiên hùng vĩ, khám phá những nét độc đáo trong văn hóa và ẩm thực nơi đây. Để có chuyến thăm quan trọn vẹn nhất, du khách hãy tìm hiểu những tour du lịch Canada tại Bunichi để chọn cho mình tour phù hợp nhất.','5', '/images/tour/canada.jpg', false),
		('Du lịch Hạ Long', '1 ngày', '2022-12-30', '2','Sunny Hotel', '452', 'Máy bay' ,'Trong nước', '5-8', 'Vịnh Hạ Long được thiên nhiên ưu ái ban tặng cho vẻ đẹp tự nhiên với non nước hùng vĩ, đẹp tựa như một bức tranh sơn thủy bí ẩn và thơ mộng. Mảnh đất được UNESCO công nhận là kỳ quan thiên nhiên thế giới. Vịnh Hạ Long như một bức tranh thuỷ mặc khổng lồ vô cùng sống động.','7', '/images/tour/halong.jpg', false),
		('Khám phá Nha Trang', '8 ngày', '2022-6-9', '5', 'Lovely', '531', 'Tàu hỏa' ,'Trong nước', '9-12', 'Tour Nha Trang 8 Ngày 7 đêm Gồm Các Điểm Du Lịch Nổi Tiếng Là: Đảo Hòn Mun, Làng Chài, Đảo Hòn Tằm nổi tiếng, Tắm bùn khoáng nóng, Tham quan Hang Rái, Đồng Cừu, Vườn Nho, Vĩnh Hy, Tháp Bà Ponagar, Hòn Chồng, Chùa Long Sơn. Khám Phá Chợ Đêm Nha Trang, Thưởng thức Ẩm thực - đặc sản Nha Trang, Khách sạn 5 sao đẳng cấp gần biển, gần trung tâm Nha Trang.','8', '/images/tour/nhatrang.jpg', false),
		('Tour Hồ Chí Minh', '1 ngày', '2021-5-8', '3', 'The Old Quarter', '543', 'Xe buýt' ,'Trong nước', '13-16', 'Sài Gòn là một trong những thành phố lớn tại Việt Nam, vì thế những địa điểm du lịch Sài Gòn luôn thu hút một lượng lớn du khách trong và ngoài nước tham quan. Rất nhiều những điểm đến độc đáo với đa dạng các hoạt động hứa hẹn sẽ mang đến cho bạn nhiều trải nghiệm thú vị.','4', '/images/tour/hochiminh.jpg', true),
		('Vòng quanh Tokyo', '8 ngày', '2021-8-2', '5', '東京ホテル', '510', 'Tàu thủy' ,'Nước ngoài', '13-16', 'Tokyo, Nhật Bản là điểm đến ưa thích của nhiều du khách trên thế giới. Bởi nơi đây có sự giao thoa hài hòa giữa nét hiện đại và truyền thống, giữa sự cổ kính và trẻ trung, tạo nên vẻ đẹp đặc biệt ở Tokyo trong mắt khách quốc tế. Dưới đây là vài điểm đến nổi tiếng ở Tokyo mà bạn không nên bỏ qua khi đặt chân đến xứ sở Mặt trời mọc.','5', '/images/tour/tokyo.jpg', false),
		('Trải nghiệm Nhật Bản', '7 ngày', '2021-2-2', '4',  '日本橋', '700', 'Tàu thủy' ,'Nước ngoài', '1-4', 'Đất nước "Mặt trời mọc" Nhật Bản luôn hấp dẫn du khách bởi sự giao thoa hài hòa giữa văn hóa đậm chất phương Đông với phồn hoa của văn minh hiện đại. Còn gì tuyệt vời hơn một lần trong đời được ngắm những cánh hoa anh đào hay băng qua những con đường đầy màu sắc của mùa lá đỏ.Tour du lịch Nhật Bản của Du Lịch Việt là sự kết hợp giữa trải nghiệm và khám phá. Tại đây du khách được tìm hiểu văn hóa xứ Phù Tang với các lễ hội văn hóa truyền thống, tinh hoa ẩm thực, khí chất samurai khi tham quan các điểm du lịch nổi tiếng.','10', '/images/tour/nhatban.jpg', true),
		('Leo đỉnh Everest', '8 ngày', '2022-7-2', '1',  'The Nature Hotel', '330', 'Máy bay' ,'Nước ngoài', '9-12', 'Kể từ khi có người đầu tiên đã chinh phục đỉnh Everest vào năm 1953, hành trình leo ngọn núi cao nhất thế giới đã có rất nhiều sự thay đổi. Ngày nay, đã có hàng trăm nhà leo núi đã chinh phục và đạt được kỳ tích này nhờ vào các kiến thức, kỹ thuật và cả đường hướng lên đỉnh đã được dựng nên cho những ai can đảm dám đương đầu với rủi ro và phải chịu một khoản chi phí đắt đỏ. Hãy cùng Bunichi tìm hiểu về đỉnh núi Everest dành cho những ai đang có ý định du lịch Everest và chinh phục đỉnh núi này.','11', '/images/tour/everest.jpg', false),
        ('Tour Honolulu','7 ngày','2022-2-11', '5', 'Khách sạn khu nghỉ dưỡng Waikiki', '750', 'Máy bay' ,'Nước ngoài', '9-12', 'Honolulu không chỉ là khu nghỉ mát đầy nắng của Waikiki, nơi những bãi cát trắng trải dài đến tận Diamond Head mang tính biểu tượng. Thành phố thủ đô là trái tim thương mại và đô thị của Hawaii, với các bảo tàng hạng nhất, khu mua sắm, ăn uống, câu lạc bộ và quán bar. Và hàng năm, hàng triệu du khách từ khắp nơi trên thế giới tìm đến Honolulu, nơi ván lướt sóng, cảnh hoàng hôn, bơi lội và sống chậm lại đơn giản là một cách sống.','14', '/images/tour/honolulu.jpg', false),
		('Tour Las Vegas','10 ngày','2022-2-1', '5', 'Polo Towers By Diamond Resorts', '800', 'Máy bay' ,'Nước ngoài', '13-16', 'Vegas là khung cảnh bữa tiệc lung linh ở rìa sa mạc Mojave. Các trò chơi giải trí hạng A, sòng bạc hào nhoáng và nhà hàng đầu bếp nổi tiếng là những trò chơi trong thị trấn, nhưng cảnh đẹp ngoạn mục cách Strip không xa — Đập Hoover và Hẻm núi Red Rock cách đó chưa đầy một giờ.','21', '/images/tour/las-vegas.jpg', false),
		('Tour Dubai','3 ngày','2022-2-14', '4', 'Ascott Park Place Duba', '100', 'Tàu hỏa' ,'Nước ngoài', '1-4', 'Là trung tâm hàng không lớn và là điểm dừng chân cuối cùng, Dubai thu hút du khách bằng khu mua sắm, kiến ​​trúc tương lai — bao gồm cả toà nhà cao nhất thế giới, Burj Khalifa — và sự pha trộn giữa văn hoá thế giới cũ và các điểm tham quan hàng đầu. Tìm kiếm các món hời trong các khu chợ cổ kính của khu phố cổ và các thương hiệu sang trọng tại một trung tâm mua sắm 1.200 cửa hàng; lặn với bình dưỡng khí bên trong một bể lớn tại Dubai Aquarium và Underwater Zoo hoặc trượt dốc tại công viên tuyết trong nhà Ski Dubai; đi du thuyền trên thuyền dhow truyền thống; và đi vào sa mạc để đào cồn trong chiếc xe 4x4, sau đó ăn tối dưới những vì sao trong trại Bedouin truyền thống.','3', '/images/tour/dubai.jpg', false),
		('Tour London','6 ngày','2022-2-2', '3', 'The Landmark London', '400', 'Máy bay' ,'Nước ngoài', '5-8', 'Được biết đến với lịch sử và kiến ​​trúc phong phú, London đã thu hẹp khoảng cách để trở thành một trung tâm văn hóa sôi động với các bảo tàng, quán ăn và các buổi biểu diễn đẳng cấp thế giới.','10', '/images/tour/london.jpg', false),
		('Tour Amsterdam','12 ngày','2022-2-1', '5', 'Khách sạn Eden Amsterdam', '500', 'Máy bay' ,'Nước ngoài', '9-12', 'Hà Lan tràn ngập vẻ đẹp và sự phiêu lưu, từ những chiếc cối xay gió đẹp như tranh vẽ và những cánh đồng hoa tulip đẹp đến ngỡ ngàng cho đến thủ đô Amsterdam thời thượng, trung tâm về nghệ thuật. Là khu vực đông dân nhất của Hà Lan, Amsterdam lịch sử nổi tiếng với những con đường cổ kính và kênh đào tuyệt đẹp.  Khám phá truyền thống nghệ thuật phong phú của Amsterdam bằng cách tham quan các bảo tàng mang tính biểu tượng như Rijksmuseum và Van Gogh Museum; các tour du lịch cung cấp quyền truy cập thẳng vào các điểm tham quan phổ biến này.','1', '/images/tour/amsterdam.jpg', false),
		('Tour Barcelona','14 ngày','2022-3-23', '3', 'Khách sạn Paxton Barcelona', '700', 'Tàu hỏa' ,'Nước ngoài', '1-4', 'Một trong những thành phố quốc tế nhất của Tây Ban Nha, Barcelona thu hút du khách với kiến ​​trúc kỳ lạ, những bãi biển Địa Trung Hải và khung cảnh ăn uống suốt ngày đêm. Theo chân thiên tài nghệ thuật của cựu cư dân Antoni Gaudí quanh thành phố, từ Parc Güell đến Casa Battló đến viên ngọc quý của ông: Sagrada Familia vẫn chưa hoàn thành. Làm quen với ẩm thực địa phương tại Chợ La Boqueria, sau đó ăn uống theo cách của bạn qua các quán bar và nhà hàng ở các khu vực lân cận như Khu phố Gothic và Gràcia. Theo dõi một trận đấu bóng đá tại Camp Nou để xem FC Barcelona được tôn sùng như thế nào ở cấp độ gần như tôn giáo. Và nếu bạn còn chút năng lượng, hãy tổ chức tiệc cho đến khi mặt trời mọc tại một câu lạc bộ nhiều tầng.','34', '/images/tour/barcelona.jpg', false),
		('Tour Miami','5 ngày','2022-2-28', '4', 'Khách sạn AKA Brickell', '560', 'Tàu thủy' ,'Nước ngoài', '5-8', 'Những bãi biển bình dị, màu sắc rực rỡ, kiến ​​trúc trang trí nghệ thuật, những khu vực đậm chất Cuba, cuộc sống về đêm phong phú - hóa ra, Miami không chỉ là một nơi nghỉ ngơi đầy nắng.','40', '/images/tour/miami.jpg', false),
		('Tour Denmark','8 ngày','2022-1-31', '5', 'Biệt thự Wunder', '600', 'Xe buýt' ,'Nước ngoài', '9-12', 'Nằm trên bán đảo Jutland và một quần đảo bao gồm hơn 400 hòn đảo, Đan Mạch nổi tiếng về cuộc sống chất lượng cao và tư duy hướng tới sức khỏe. Copenhagen, thủ đô của đất nước Scandinavia, là một trong những thành phố thân thiện với xe đạp nhất thế giới, vì vậy các chuyến đi xe đạp là một cách tự nhiên để ngắm cảnh, từ Cung điện Amalienborg sang trọng và Pháo đài Kastellet đến Lâu đài Rosenborg và bức tượng Nàng tiên cá nổi tiếng được lấy cảm hứng từ Hans Christian Andersen. Các chuyến tham quan bằng xe buýt Hop-on hop-off cho phép bạn kết hợp và kết hợp từ nhiều điểm tham quan nằm rải rác trên các vùng lân cận đa dạng của thành phố, bao gồm cả vùng lân cận Christiania, nơi về cơ bản hoạt động giống như một quốc gia độc lập.','13', '/images/tour/denmark.jpg', false),
		('Tour Ninh Binh','14 ngày','2022-1-12', '2', 'Khách sạn khu nghỉ dưỡng Tam Cốc Garden', '1000', 'Tàu hỏa' ,'Trong nước', '13-16', 'Ninh Bình, nằm ở đồng bằng sông Hồng của miền Bắc Việt Nam, là một cơ sở lý tưởng để khám phá phong cảnh núi đá vôi gần đó, đặc biệt là tại Tam Cốc','20', '/images/tour/ninh-binh.jpg', false),
		('Tour Đảo Phú Quốc','14 ngày','2022-1-12', '2', 'Khách sạn khu nghỉ dưỡng Novotel Phu Quoc', '1000', 'Tàu thủy' ,'Trong nước', '13-16', 'Phú Quốc là hòn đảo lớn nhất Việt Nam, nằm trong vịnh Thái Lan. Đảo Phú Quốc cùng với các đảo nhỏ hơn ở lân cận và quần đảo Thổ Chu nằm cách đó 55 hải lý về phía tây nam hợp thành thành phố Phú Quốc thuộc tỉnh Kiên Giang. Đây cũng là thành phố đảo đầu tiên của Việt Nam','20', '/images/tour/phu-quoc.jpg', false);

